// Extraire les feeds RSS de chaque topic
const items = $input.all();
const results = [];

for (const item of items) {
  const topic = item.json;
  if (!topic || !topic.rss_feeds) continue;

  const feeds = topic.rss_feeds || [];

  for (const feedUrl of feeds) {
    results.push({
      json: {
        topic_id: topic.topic_id,
        topic_name: topic.topic_name,
        user_id: topic.user_id,
        keywords_fr: topic.keywords_fr || [],
        keywords_en: topic.keywords_en || [],
        feed_url: feedUrl
      }
    });
  }
}

if (results.length === 0) {
  return [{ json: { error: 'No feeds found' } }];
}
return results;

// Parser les articles RSS et preparer les donnees
const items = $input.all();
const feedInfo = $('Extract Feeds').first().json;
const results = [];

for (const item of items) {
  if (!item.json.link || !item.json.title) continue;

  const title = item.json.title.trim();
  let content = item.json.content || item.json.description || '';
  content = content.replace(/<[^>]*>/g, ' ')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/\s+/g, ' ')
    .trim()
    .substring(0, 3000);

  results.push({
    json: {
      topic_id: feedInfo.topic_id,
      topic_name: feedInfo.topic_name,
      user_id: feedInfo.user_id,
      keywords_fr: feedInfo.keywords_fr,
      keywords_en: feedInfo.keywords_en,
      feed_url: feedInfo.feed_url,
      title: title,
      url: item.json.link,
      content_rss: content,
      author: item.json.creator || item.json.author || null,
      published_at: item.json.pubDate || item.json.isoDate || new Date().toISOString(),
      image_url: item.json.enclosure?.url || null,
      source_name: feedInfo.feed_url.match(/https?:\/\/([^/]+)/)?.[1] || 'Unknown'
    }
  });
}

// Limiter a 10 articles par feed
return results.slice(0, 10);

// Combiner les donnees de l'article avec le contenu Jina
const articleData = $('Loop Articles').first().json;
const jinaResponse = $input.first().json;

// Utiliser le contenu Jina si disponible, sinon le contenu RSS
let content = jinaResponse.data || articleData.content_rss || '';
content = content.substring(0, 8000); // Limiter la taille

// Combiner les mots-cles
const keywords = [
  ...(articleData.keywords_fr || []),
  ...(articleData.keywords_en || [])
].join(', ');

return [{
  json: {
    topic_id: articleData.topic_id,
    topic_name: articleData.topic_name,
    user_id: articleData.user_id,
    keywords: keywords,
    feed_url: articleData.feed_url,
    title: articleData.title,
    url: articleData.url,
    content: content,
    author: articleData.author,
    published_at: articleData.published_at,
    image_url: articleData.image_url,
    source_name: articleData.source_name
  }
}];

