// Extraire le score de pertinence
const articleData = $('Extract Summary').first().json;
const relevanceResponse = $input.first().json;

let relevanceScore = 50; // Valeur par defaut
if (relevanceResponse?.response) {
  const match = relevanceResponse.response.match(/\d+/);
  if (match) {
    relevanceScore = Math.min(100, Math.max(0, parseInt(match[0])));
  }
}

return [{
  json: {
    topic_id: articleData.topic_id,
    topic_name: articleData.topic_name,
    user_id: articleData.user_id,
    keywords: articleData.keywords,
    feed_url: articleData.feed_url,
    title: articleData.title,
    url: articleData.url,
    content: articleData.content,
    author: articleData.author,
    published_at: articleData.published_at,
    image_url: articleData.image_url,
    source_name: articleData.source_name,
    summary: articleData.summary,
    relevance_score: relevanceScore
  }
}];

