// Extraire les tags et preparer les donnees finales
const articleData = $('Extract Relevance').first().json;
const tagsResponse = $input.first().json;

// Parser les tags
let tags = [];
if (tagsResponse?.response) {
  tags = tagsResponse.response
    .split(',')
    .map(t => t.trim().toLowerCase().replace(/[^a-z0-9-]/g, ''))
    .filter(t => t.length > 0 && t.length < 30)
    .slice(0, 5);
}

return [{
  json: {
    topic_id: articleData.topic_id,
    user_id: articleData.user_id,
    title: articleData.title,
    url: articleData.url,
    content: articleData.content,
    summary: articleData.summary,
    source: articleData.source_name,
    published_date: articleData.published_at,
    relevance_score: articleData.relevance_score,
    tags: tags,
    read_status: false,
    bookmarked: false
  }
}];
